import React, { Component } from 'react'

class PostForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            body : '',
            title : ''
        };
    }


    render() {

        return (
            <div>
                <h3>Add Post</h3>
                <form>
                    <div>
                        <label>
                            Title: 
                        </label>
                        <br />
                        <input type="text" name="title" value={ this.state.title }/>
                    </div>
                    <br />
                    <div>
                        <label>
                            Body: 
                        </label>
                        <br />
                        <textarea name="body" value={ this.state.body }/>
                    </div>
                    <br />
                    <div>
                        <button type="submit">
                            Submit
                        </button>
                    </div>
                </form>
            </div>
        )
    }
}

export default PostForm;
