import { FETCH_POSTS, NEW_POST } from './types';

export const fetchPosts = () => dispatch => {
    fetch('https://jsonplaceholder.typicode.com/posts')
        .then(res => res.json())
        .then(posts => dispatch({  // Dispatch fetchPosts to postReducer
            type: FETCH_POSTS,
            payload: posts
        }))
    
};

export const createPost = () => dispatch => {
    fetch('https://jsonplaceholder.typicode.com/posts', {
            method: 'POST',
            headers: {
                'content-type': 'application/json'
            },
            body: JSON.stringify(post)
        })
            .then(res => res.json())
            .then(post => dispatch({  // Dispatch createPost to postReducer
            type: NEW_POST,
            payload: post
        }));
    
};
